package brain.stroke.apk.app;

import brain.stroke.apk.list.LinkedList;

import static brain.stroke.apk.utilities.StringUtils.join;
import static brain.stroke.apk.utilities.StringUtils.split;
import static brain.stroke.apk.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
